package code.slidingwindow.adityaVerma;

import java.util.HashMap;
import java.util.List;
import java.util.Map;

import static utils.AnagramGenerator.generateAnagram;

public class CountOccOfAnagram {
    public static void main(String[] args) {
//        System.out.println(bruteForce("aabaabaa", "aaba"));
//        String s = "aabaabaa";
//        String pattern = "aaba";
        String s = "";
        String pattern = "";
//        String str = "a(10k times)ba(10k times)";
//        String p = "a(10k times)";
        System.out.println(optimized3(s, pattern));
    }

    // T.C: O(n*k)
    private static int optimized(String s, String pattern) {
        int i = 0, j = 0, count = 0;
        Map<Character, Integer> charMap = new HashMap<>();
        Map<Character, Integer> patternMap = new HashMap<>();

        // Generate the patternMap
        for (char ch : pattern.toCharArray()) patternMap.put(ch, patternMap.getOrDefault(ch, 0) + 1);
        System.out.println(patternMap);

        while (j < s.length()) {

            if (patternMap.containsKey(s.charAt(j))) {
                charMap.put(s.charAt(j), charMap.getOrDefault(s.charAt(j), 0) + 1);
            }

            if (j - i + 1 < pattern.length()) j++;

            else if (j - i + 1 == pattern.length()) {
                boolean same = true;
                for (Map.Entry entry : patternMap.entrySet()) {
                    if (!charMap.containsKey(entry.getKey()) || !(charMap.get(entry.getKey()).equals(entry.getValue()))) {
                        same = false;
                    }
                }
                if (same) count++;

                if (charMap.containsKey(s.charAt(i))) {
                    charMap.put(s.charAt(i), charMap.get(s.charAt(i)) - 1);
                }
                j++;
                i++;
            }

        }

        return count;
    }

    // T.C: O(n)
    // S.C: O(k) , where k is the distinct characters in the pattern.
    private static int optimized2(String s, String pattern) {
        Map<Character, Integer> patternMap = new HashMap<>();
        // Generate the patternMap
        for (char ch : pattern.toCharArray()) patternMap.put(ch, patternMap.getOrDefault(ch, 0) + 1);

        int i = 0, j = 0, count = patternMap.size(), ans = 0;

        while (j < s.length()) {
            char ch = s.charAt(j);
            if (patternMap.containsKey(ch)) {
                patternMap.put(ch, patternMap.get(ch) - 1);
                if (patternMap.get(ch).equals(0)) count--;
            }


            if (j - i + 1 < pattern.length()) j++;

            else if (j - i + 1 == pattern.length()) {
                if (count == 0) {
                    ans++;
                }
                ch = s.charAt(i);
                if (patternMap.containsKey(ch)) {
                    patternMap.put(ch, patternMap.get(ch) + 1);
                    if (patternMap.get(ch).equals(1)) count++;
                }
                j++;
                i++;
            }

        }

        return ans;
    }

    // T.C: O(n * 26) = O(n)
    // S.C: O(1)
    private static int optimized3(String s, String pattern) {

        char[] charMap = new char[26];
        char[] patternMap = new char[26];

        int i = 0, j = 0, count = 0, k = pattern.length();

        for (char ch : pattern.toCharArray()) patternMap[ch - 'a']++;

        while (j < s.length()) {
            char ch = s.charAt(j);
            charMap[ch - 'a']++;

            if (j - i + 1 < k) j++;

            else if (j - i + 1 == k) {
                if (checkBothBooleanArrayAreEqual(charMap, patternMap)) count++;
                ch = s.charAt(i);
                charMap[ch - 'a']--;
                i++;
                j++;
            }
        }
        return count;
    }

    private static boolean checkBothBooleanArrayAreEqual(char[] a, char[] b) {
        for (int i = 0; i < a.length; i++) {
            if (a[i] != b[i]) return false;
        }
        return true;
    }

    // T.C: O(n!)->for generating anagrams  + O(n^2)->for generating substrings
    // S.C: O(n!)
    private static int bruteForce(String s, String pattern) {
        List<String> anagrams = generateAnagram(pattern);
        int count = 0;
        for (int i = 0; i < s.length() - pattern.length() + 1; i++) {

            StringBuilder sb = new StringBuilder();
            for (int j = i; j < pattern.length() + i; j++) {
                sb.append(s.charAt(j));
            }

            if (anagrams.contains(sb.toString())) count++;
        }
        return count;
    }
}
