package code.slidingwindow.adityaVerma;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * Leetcode: 438. Find All Anagrams in a String
 */
public class FindAllAnagrams {

    public static void main(String[] args) {
        String s = "";
        String pattern = "";
        System.out.println(findAnagrams(s, pattern));
    }

    public static List<Integer> findAnagrams(String s, String p) {
        return optimized(s, p);
    }

    private static List<Integer> optimized(String s, String pattern) {
        int i = 0, j = 0;

        List<Integer> indexes = new ArrayList<Integer>();
        Map<Character, Integer> charMap = new HashMap<>();
        Map<Character, Integer> patternMap = new HashMap<>();

        // Generate the patternMap
        for (char ch : pattern.toCharArray()) patternMap.put(ch, patternMap.getOrDefault(ch, 0) + 1);

        while (j < s.length()) {

            if (patternMap.containsKey(s.charAt(j))) {
                charMap.put(s.charAt(j), charMap.getOrDefault(s.charAt(j), 0) + 1);
            }

            if (j - i + 1 < pattern.length()) j++;

            else if (j - i + 1 == pattern.length()) {
                boolean same = true;
                for (Map.Entry entry : patternMap.entrySet()) {
                    // Use .equals for integer comparasion since the value greater 127 is not cached and object comparsion will
                    // happen and testcases would fail.
                    if (!charMap.containsKey(entry.getKey()) || !(charMap.get(entry.getKey()).equals(entry.getValue()))) {
                        same = false;
                    }
                }
                if (same) indexes.add(i);
                if(charMap.containsKey(s.charAt(i))){
                    charMap.put(s.charAt(i), charMap.get(s.charAt(i)) - 1);
                }
                j++;
                i++;
            }

        }

        return indexes;
    }
}
